document.addEventListener("DOMContentLoaded",()=>{const e=document.querySelector(".search__input"),n=document.querySelectorAll(".search__checkbox");e.addEventListener("input",()=>{const c=e.value.toLowerCase();n.forEach(t=>{const o=t.querySelector(".checkbox-search__text").textContent.toLowerCase();t.style.display=o.includes(c)?"":"none"}),window.dispatchEvent(new Event("resize"))})});
